export default {
  name: 'InputDate',
  componentName: 'InputDate',
  props: {
    value: String,
    label: String,
    style_component: {
      type: Object,
      default: null
    },
    isError: {
      type: Boolean,
      defaul: false
    },
    disabled: {
      type: Boolean,
      defaul: false
    },
    type: {
      type: String,
      default: 'date'
    }
  },

  data() {
    return {
      value_format: 'dd.MM.yyyy',
      format: 'dd.MM.yyyy',
      focused: false
    };
  },

  created() {
    if (this.type === 'datetime') {
      // TODO: check it fix for value_forma / format
      this.value_format = 'dd.MM.yyyy_HH:mm';
      this.format = 'dd.MM.yyyyHH:mm';
    } else {
      // date
      this.value_format = 'dd.MM.yyyy';
      this.format = 'dd.MM.yyyy';
    }
  },

  methods: {
    handleInput(value) {
      console.log(value);

      if (value === null) {
        this.$emit('input', '');
      } else {
        if (this.type === 'datetime') {
          this.$emit('input', new Date(value.getTime() + value.getTimezoneOffset() * 60 * 1000 * 2).toISOString().substr(0, 19));
        } else {
          // date
          this.$emit('input', new Date(value.getTime() - value.getTimezoneOffset() * 60 * 1000).toISOString().split('T')[0]);
        }
      }
    }

  }
};
