import Handlebar from 'icons/Handlebar.svg';
import DropdownComponent from '@/components/dropdown/DropdownComponent.vue';
import ActionIcon from 'icons/Action.svg';
export default {
  name: 'HeadBlock',
  componentName: 'HeadBlock',
  components: {
    Handlebar,
    ActionIcon,
    DropdownComponent
  },
  props: {
    indexComponent: {
      type: Number,
      required: true
    },
    status: {
      type: String,
      default: null
    }
  },

  data() {
    return {};
  },

  computed: {
    dropdownItems() {
      if (this.status === 'NOT_CHANGED') {
        return [this.$t('$dropdown.start'), this.$t('$dropdown.delete')];
      } else if (this.status === 'NEW') {
        return [this.$t('$dropdown.delete')];
      } else if (this.status === 'DELETE') {
        return [this.$t('$dropdown.cancel')];
      } else if (this.status === 'UPDATE') {
        return [this.$t('$dropdown.delete')];
      } else if (this.status === 'START') {
        return [this.$t('$dropdown.cancel'), this.$t('$dropdown.delete')];
      } else {
        return [this.$t('$dropdown.cancel'), this.$t('$dropdown.start'), this.$t('$dropdown.delete')];
      }
    }

  },
  methods: {
    handleDropdown(indexDropdown) {
      console.log("[MaterialPromocode.vue]: handleDropdown: ", indexDropdown);

      switch (indexDropdown) {
        case 0:
          if (this.status === 'NOT_CHANGED') {
            this.$emit('onStatus', 'START');
          } else if (this.status === 'NEW') {
            this.$emit('onDelete');
          } else if (this.status === 'UPDATE') {
            this.$emit('onStatus', 'DELETE');
          } else {
            this.$emit('onStatus', 'UPDATE');
          }

          break;

        case 1:
          if (this.status === 'NOT_CHANGED' || this.status === 'START') {
            this.$emit('onStatus', 'DELETE');
          } else {
            this.$emit('onStatus', 'START');
          }

          break;

        case 2:
          this.$emit('onStatus', 'DELETE');
          break;
      }
    }

  }
};
