import HeadBlock from '@/components/elements/fields/block/HeadBlock.vue'; // import HeadContent from '@/components/Fields/Additional/HeadContent.vue'
// import InputLangComponent from '@/components/Fields/InputLocaleComponent.vue'

import TextareaComponent from '@/components/elements/fields/TextareaComponent.vue'; // import MarkdownLocaleComponent from '@/components/Fields/Additional/MarkdownLocaleComponent.vue'

export default {
  name: 'ScriptBlock',
  componentName: 'ScriptBlock',
  components: {
    HeadBlock: HeadBlock,
    TextareaComponent: TextareaComponent
  },
  props: {
    value: {
      type: Object,
      default: null
    },
    indexComponent: {
      type: Number,
      required: true
    }
  },
  data: function data() {
    return {
      curValue: '',
      statuses: ['DELETE', 'START', 'NEW', 'UPDATE', 'NOT_CHANGED']
    };
  },
  watch: {
    value: {
      handler: function handler(newValue, oldValue) {
        if (!!newValue && newValue.hasOwnProperty("text")) {
          this.curValue = newValue.text;
        }
      },
      immediate: true
    }
  },
  computed: {
    getStatus: function getStatus() {
      if (!!this.value && this.value.hasOwnProperty('status') && this.statuses.indexOf(this.value.status) !== -1) {
        return this.value.status;
      }

      return null;
    }
  },
  methods: {
    inputHandle: function inputHandle(value) {
      var tmp = this.$panel.clone(this.value);
      tmp.text = value;

      if (tmp.status !== 'NEW') {
        tmp.status = 'UPDATE';
      }

      this.$emit('input', tmp);
    },
    changeStatusHandle: function changeStatusHandle(status) {
      var tmp = this.$panel.clone(this.value);
      tmp.status = status;
      this.$emit('input', tmp);
    }
  }
};
