import InformationPanel from '@/components/controlPanels/InformationPanel.vue';
import InfoViewSection from '@/components/sections/InfoViewSection.vue';
export default {
  name: 'PageInformation',
  componentName: 'PageInformation',
  components: {
    //     TitlePage,
    //     BlocksComponent,
    InfoViewSection: InfoViewSection,
    InformationPanel: InformationPanel
  },
  data: function data() {
    return {
      isShowData: false,
      material: {
        packageInfo: null,
        systemInfo: null
      }
    };
  },
  computed: {
    getBios: function getBios() {
      return "".concat(this.material.packageInfo.bios.vendor, "\n\u0412\u0435\u0440\u0441\u0438\u044F: ").concat(this.material.packageInfo.bios.version, "\n ") + this.material.packageInfo.bios.date.replace(/(\d{2})\/(\d{2})\/(\d{4})/, "$3-$2-$1").toDateMyFormat(0, this);
    },
    getRam: function getRam() {
      return "\u0412\u0441\u0435\u0433\u043E \u041E\u041F: ".concat(Math.round(this.material.systemInfo.Totalram / Math.pow(1024, 3)), " GB\n") + "\u0414\u043E\u0441\u0442\u0443\u043F\u043D\u044B\u0439: ".concat(Math.round(this.material.systemInfo.Freeram / Math.pow(1024, 2)), " MB\n") + "\u0412\u0441\u0435\u0433\u043E \u043F\u043E\u0434\u043A\u0430\u0447\u043A\u0438: ".concat(Math.round(this.material.systemInfo.Totalswap / Math.pow(1024, 2)), " MB\n") + "\u0414\u043E\u0441\u0442\u0443\u043F\u043D\u043E \u043F\u043E\u0434\u0441\u043A\u0430\u0447\u043A\u0438: ".concat(Math.round(this.material.systemInfo.Freeswap / Math.pow(1024, 2)), " MB\n");
    },
    getNode: function getNode() {
      return "\u0425\u043E\u0441\u0442: ".concat(this.material.packageInfo.node.hostname, "\n") + "".concat(this.material.packageInfo.node.machineid, "\n") + "\u0427\u0430\u0441\u043E\u0432\u043E\u0439 \u043F\u043E\u044F\u0441: ".concat(this.material.packageInfo.node.timezone, "\n");
    },
    getBoard: function getBoard() {
      return [[{
        name: 'assettag',
        text: this.material.packageInfo.board.assettag
      }, {
        name: 'name',
        text: this.material.packageInfo.board.name
      }, {
        name: 'vendor',
        text: this.material.packageInfo.board.vendor
      }, {
        name: 'version',
        text: this.material.packageInfo.board.version
      }], [{
        name: 'assettag',
        text: this.material.packageInfo.chassis.assettag
      }, {
        name: 'type',
        text: this.material.packageInfo.chassis.type
      }, {
        name: 'vendor',
        text: this.material.packageInfo.chassis.vendor
      }, {
        name: 'version',
        text: this.material.packageInfo.chassis.version
      }]];
    },
    getStorage: function getStorage() {
      return this.material.packageInfo.storage.map(function (storage) {
        return Object.keys(storage).map(function (key) {
          return {
            name: key,
            text: storage[key]
          };
        });
      });
    },
    getCPUKernel: function getCPUKernel() {
      return [[{
        name: 'model',
        text: this.material.packageInfo.cpu.model
      }, {
        name: 'cpus / cores / threads',
        text: "".concat(this.material.packageInfo.cpu.cpus, " / ").concat(this.material.packageInfo.cpu.cores, " / ").concat(this.material.packageInfo.cpu.threads)
      }, {
        name: 'vendor',
        text: this.material.packageInfo.cpu.vendor
      }], [{
        name: 'architecture',
        text: this.material.packageInfo.kernel.architecture
      }, {
        name: 'release',
        text: this.material.packageInfo.kernel.release
      }, {
        name: 'version',
        text: this.material.packageInfo.kernel.version
      }]];
    }
  },
  created: function created() {
    this.fetchData();
  },
  methods: {
    fetchData: function fetchData() {
      var _this = this;

      this.$store.state.axios.get('/information').then(function (response) {
        console.log("[Information.vue]: fetchData(response): ", response);

        if (response.status === 200) {
          _this.material = response.data;
          _this.isShowData = true;
        }
      }).catch(function (error) {
        console.log("[Information.vue]: fetchData(error): ", error);
      });
    }
  }
};
