import Maxlength from '@/utils/mixins/maxLength.js';
export default {
  name: 'InputComponent',
  componentName: 'InputComponent',
  mixins: [Maxlength],
  props: {
    value: String,
    placeholder: String,
    label: String,
    text: String,
    style_component: {
      type: Object,
      default: null
    },
    isError: {
      type: Boolean,
      defaul: false
    },
    disabled: {
      type: Boolean,
      defaul: false
    },
    crop: {
      type: Boolean,
      default: true
    },
    upperAllCase: {
      type: Boolean,
      default: false
    }
  },
  data: function data() {
    return {
      curValue: '',
      focused: false,
      idObject: ''
    };
  },
  watch: {
    value: {
      handler: function handler(newValue, oldValue) {
        // this.curValue = newValue;
        this.updateCounter(newValue);
      },
      immediate: true
    }
  },
  created: function created() {
    console.log("CREATED INPUT");
    this.$store.commit('incObjectID');
    this.idObject = this.$store.getters.getObjectID; // this.updateCounter(this.value);
  },
  methods: {
    handleFocus: function handleFocus(event) {
      this.focused = true;
    },
    handleBlur: function handleBlur(event) {
      this.focused = false;
    },
    handleInput: function handleInput(event) {
      console.log("[InputComponent.vue]: handleInput: ", event); // this.curValue = event.target.value;

      this.$emit('input', this.upperAllCase ? event.target.value.toUpperCase() : event.target.value);
    },
    handleChange: function handleChange(event) {
      console.log("[InputComponent.vue]: handleChange: ", event); // this.curValue = event.target.value;

      this.$emit('input', this.upperAllCase ? event.target.value.toUpperCase() : event.target.value);
    }
  }
};
