result:
.image-element {
    position: relative;
    width: 100%;
    height: 100%;
    //line-height: 0
    @include user-select(none);
    &__img {
        width: 100%;
        height: 100%;
        -o-object-fit: cover;
        object-fit: cover;
        border-radius: inherit;
        object-position: center top;
    }
    &__priview {
        position: absolute;
        z-index: 1;
        border-radius: inherit;
    }
    &__original {
        position: relative;
        z-index: 2;
        border-radius: inherit;
    }
    &__no-img {
        width: 100%;
        height: 100%;
        border-radius: inherit;
    }
}
