#page-scripts {
    width: 100%;
    height: calc(100vh - 60px);
    box-sizing: border-box;
    padding-bottom: 100px;
    .scripts {
        &__title {
            position: -webkit-sticky; /* Required for Safari */
            position: sticky;
            top: -20px;
            z-index: 100;
        }
        &__content {
            width: 100%;
            &-left {
                width: 820px;
            }
            &-right {
                width: 400px;
                margin-left: 20px;
                margin-top: 20px;
            }
        }
        &__section {
            margin-top: 20px;
            width: 100%;
            background-color: $white;
            min-height: 500px;
        }
        &__panel-control {
            position: -webkit-sticky; /* Required for Safari */
            position: sticky;
            top: 100px;
        }
    }
}
