export default {
    name: 'MaterialTitle',
    componentName: 'MaterialTitle',
    props: {
        value: {
            type: Number,
            default: null
        },
        direction: {
            type: Boolean,
            default: false
        },
        materialTitle: Object,
        disabled: {
            type: Boolean,
            default: false
        }
    },
    methods: {
        handleClick(index) {
            if (!this.disabled && !this.materialTitle.elements[index].disabled) this.$emit('input', index);
        }
    }
}
