.input-component {
    width: 100%;
    position: relative;
    padding: 0;
    &__label {
        width: 100%;
        padding: 10px 0;
    }
    &__inner {
        width: calc(100% - 30px);
        height: 38px;
        border: 1px solid $grey10;
        padding: 0 14px;
        &:hover {
            border: 1px solid $grey33;
        }
    }
    &__input {
        flex: 1;
        width: 100%;
        height: 100%;
        box-sizing: border-box;
        border: none;
        outline: none;
    }
    &__counter , &__text {
        margin-left: 10px;
        margin-top: 12px;
        margin-bottom: 11px;
    }
    &.is-focused {
        .input-component {
            &__inner {
                background-color: $white;
                border: 1px solid $grey33;
            }
            &__input {
                background-color: $white;
            }
        }
    }
    &.is-disabled {
        color: $grey50;
        .input-component {
            &__inner {
                border: 1px solid $grey10;
                &:hover {
                    border: 1px solid $grey10;
                }
            }
            &__input {
                cursor: not-allowed;
                color: $grey50;
            }
        }
    }
}



