#page-scripts {
    position: relative;
    top: 0;
    left: 0;
    width: 100%;
    height: calc(100vh - 60px);
    box-sizing: border-box;
    padding-bottom: 60px;
    & .top-line {
        &__slot {

        }
        &__select {
            width: 250px;
        }
        &__button {
            min-width: 150px;
            padding: 10px 20px;
            box-sizing: border-box;
        }
    }
    & .material {
        &__title-names {
            border-bottom: 1px solid $grey10;
            position: -webkit-sticky; /* Required for Safari */
            position: sticky;
            top: -20px;
            z-index: 100;
            margin-bottom: -1px;
            &-border-no {
                border-bottom: none;
                margin-bottom: 0px;
            }
        }
    }
}
