result:
.head-block {
    position: relative;
    background-color: $cyan-10;
    padding: 14px 0 14px 0;
    width: 100%;
    border-bottom: 1px solid $cyan-20;
    &__index {
        margin: 0px 10px 0 14px;
        padding: 1px 5px 0px 5px;
        background-color: $white;
        border-radius: 30px;
        min-width: 25px;
        text-align: center;
    }
    &__handlebar {
        position: absolute;
        padding: 15px 20px 15px 20px;
        top: 0px;
        // left: 379px;
        left: calc(50% - 30px);
        & .handlebar-icon--line {
            stroke: $cyan-30;
        }
    }
    &__dropdown {
        position: absolute;
        top: 10px;
        right: 14px;
        & .action-icon--{
            fill: $cyan-30;
        }
    }
}
