result:
.scripts-panel {
    width: calc(100% - 2px);
    border: 1px solid $cyan-10;
    &__title {
        height: 20px;
        padding: 19px;
    }
    &__row {
        background-color: $white;
        width: 100%;
        border-top: 1px solid $cyan-10;
        display: flex;
        &-left-p {
            flex: 1;
            width: 100%;
            color: $cyan-dark;
            display: inline-block;
            margin: 20px 0 19px 19px;
            white-space: nowrap;
        }
        &-right-p {
            display: inline-block;
            color: $cyan-20;
            margin: 20px 19px 19px 10px;
            white-space: nowrap;
        }
        &-button {
            width: calc(100% - 18px);
            margin: 10px 9px 9px 9px;
            padding: 10px 0;
        }
    }
}
